import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { ApiURL } from '../shared/Api';
import axios from 'axios';
import { Link } from 'react-router-dom';

const SingleCountry = () => {
  const [country, setCountry] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const { countryName } = useParams();

  const getCountryByName = async () => {
    const res = await axios(`${ApiURL}/name/${countryName}`);
    // if (!res.ok) throw new Error('could not found');
    const data = await res.data;
    setCountry(data);
    setLoading(false);
    try {
    } catch (error) {
      setLoading(false);
      setError(error.message);
    }
  };
  useEffect(() => {
    getCountryByName();
  }, [countryName]);
  return (
    <div className=' h-full w-full flex justify-between text-vdark-blueB dark:text-whiteT bg-light dark:bg-vdark-blueB transition duration-700'>
      <button className='text-vdark-blueB dark:text-whiteT bg-light dark:bg-vdark-blueB transition duration-700'>
        <Link to='/'>back</Link>
      </button>
      {country?.map((country, index) => (
        <article className='text-vdark-blueB dark:text-whiteT bg-light dark:bg-vdark-blueB transition duration-700'>
          <div className='text-vdark-blueB dark:text-whiteT bg-light dark:bg-vdark-blueB transition duration-700'>
            <img src={country.flags.png} alt={country.name.common} />
          </div>
          <div className='text-vdark-blueB dark:text-whiteT bg-light dark:bg-vdark-blueB transition duration-700'>
            <h5>
              Native Name: <span>{country.name.common}</span>
            </h5>
            <h5>
              Population: <span>{country.population}</span>
            </h5>
            <h5>
              Region: <span>{country.region}</span>
            </h5>
            <h5>
              Sub Region: <span>{country.subRegion}</span>
            </h5>
          </div>
        </article>
      ))}
    </div>
  );
};

export default SingleCountry;
