import React, { useEffect, useState } from 'react';
import { AiOutlineSearch } from 'react-icons/ai';

const SearchCountry = ({ onNameSearch, onCapitalSearch }) => {
  const [input, setInput] = React.useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    onNameSearch(input);
    onCapitalSearch(input);
  };

  return (
    <form
      className='flex items-center rounded-sm w-[20rem] '
      onSubmit={handleSearch}
    >
      <div className=' relative w-full '>
        <AiOutlineSearch className=' absolute top-3 left-2 text-ldark-grayI   ' />
        <input
          id='search'
          className=' w-full px-10 py-3 text-vdark-blueB dark:text-whiteT bg-whiteT dark:bg-vdark-blueE  outline-none shadow-lg rounded-sm transition duration-700 '
          type='search'
          placeholder='search for a country...'
          value={input}
          onChange={(e) => setInput(e.target.value)}
        />
      </div>
    </form>
  );
};

export default SearchCountry;
