import { useDarkMode } from '../shared/useDarkMode';
import { FaRegMoon } from 'react-icons/fa';
const Navbar = () => {
  const [colorTheme, setThemeMode] = useDarkMode();
  return (
    <nav className='w-full bg-whiteT dark:bg-vdark-blueE text-vdark-blueT dark:text-whiteT h-[10vh] flex justify-between items-center shadow-lg px-10 transition duration-700 '>
      <h1 className='tex-vdark-blueT dark:whiteT font-bold'>
        Where in the world?
      </h1>
      <div onClick={() => setThemeMode(colorTheme)}>
        {colorTheme === 'light' ? (
          <span className='flex gap-3 items-center capitalize cursor-pointer font-bold'>
            <FaRegMoon /> light mode
          </span>
        ) : (
          <span className='flex gap-3 items-center capitalize cursor-pointer font-bold'>
            <FaRegMoon /> dark mode
          </span>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
