import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import FilterRegion from './FilterRegion';
import SearchCountry from './SearchCountry';
import { ApiURL } from '../shared/Api';
import { Link } from 'react-router-dom';
import SingleCountry from '../pages/SingleCountry';

const Countries = () => {
  const [countries, setCountries] = useState([]);
  const fetchData = async () => {
    try {
      const response = await axios.get(`${ApiURL}/all`);
      const countries = response.data;
      setCountries(countries);
    } catch (error) {
      console.log(error);
    }
  };

  const getCountryName = async (countryName) => {
    try {
      const respName = await axios.get(`${ApiURL}/name/${countryName}`);

      // );
      const inputName = respName.data;

      setCountries(inputName);
    } catch (error) {
      console.log(error);
    }
  };
  const getCountryCapital = async (countryCapital) => {
    try {
      const respCapital = await axios.get(
        `${ApiURL}/capital/${countryCapital}`
      );

      const inputCapital = respCapital.data;
      // console.log(inputCapital);

      setCountries(inputCapital);
    } catch (error) {
      console.log(error);
    }
  };
  const getRegion = async (region) => {
    try {
      const respRegion = await axios.get(`${ApiURL}/region/${region}`);

      const selectRegion = respRegion.data;
      // console.log(inputCapital);

      setCountries(selectRegion);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
  return (
    <>
      <div className=' flex flex-col   md:flex-row md:justify-between   px-10 pt-10  bg-light dark:bg-vdark-blueB mt-6 dark:text-whiteT transition duration-700 text-vdark-blueT  gap-6 '>
        <SearchCountry
          onNameSearch={getCountryName}
          onCapitalSearch={getCountryCapital}
        />
        <FilterRegion onSelect={getRegion} />
      </div>
      <div className=' grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xxl:grid-cols-4     justify-center items-center gap-10  px-10 h-screen  bg-light dark:bg-vdark-blueB dark:text-whiteT transition duration-700 text-vdark-blueT  '>
        {countries?.map((country, i) => {
          return (
            <Link to={`/country/${country.name.common}`}>
              <article
                key={i}
                className='  bg-whiteT dark:bg-vdark-blueE text-vdark-blueT dark:text-whiteT shadow-lg rounded-sm pb-10 '
              >
                <img
                  className=' w-[4300px] h-[200px] rounded-t-sm'
                  src={country.flags.png}
                  alt=''
                />
                <div className='mt-6 ml-6'>
                  <h2 className=' text-xl font-bold mb-4'>
                    {country.name.common}
                  </h2>
                  <ul>
                    <li>
                      <span className=' font-[700]'>Population:</span>
                      {country.population}
                    </li>
                    <li>
                      <span className=' font-[700]'>Region:</span>
                      {country.region}
                    </li>
                    <li>
                      <span className=' font-[700]'>Capital:</span>
                      {country.capital}
                    </li>
                  </ul>
                </div>
              </article>
            </Link>
          );
        })}
      </div>
    </>
  );
};

export default Countries;
